mapper.update:
UPDATE user
SET userStatus=#{userStatus}
WHERE userId=#{userId}

mapper.select:
SELECT COUNT(*) from comment WHERE 1=1
AND comContent LIKE CONCAT('%' #{keyword}, '%')
AND comRegdate BETWEEN #{startDate} AND #{endDate}

SELECT * from comment
WHERE comDeleteStatus = 0

	AND comContent LIKE CONCAT('%' #{keyword}, '%')


	AND comRegdate BETWEEN #{startDate} AND #{endDate}

LIMIT #{start}, #{pageSize}

mapper.update:
UPDATE comment
SET comDeleteStatus = 0
WHERE commentId = #{commentId}

mapper.select:
SELECT COUNT(*) from review WHERE 1=1
AND reviewContent LIKE CONCAT('%' #{keyword}, '%')
AND reviewRegdate BETWEEN #{startDate} AND #{endDate}

SELECT * from review
WHERE 1=1

	AND reviewContent LIKE CONCAT('%' #{keyword}, '%')


	AND reviewRegdate BETWEEN #{startDate} AND #{endDate}

LIMIT #{start}, #{pageSize}

SELECT COUNT(*) from inquiry WHERE 1=1
AND inquiryTitle LIKE CONCAT('%' #{keyword}, '%')
AND inquiryRegdate BETWEEN #{startDate} AND #{endDate}

SELECT * from inquiry WHERE 1=1
AND inquiryTitle LIKE CONCAT('%' #{keyword}, '%')
AND inquiryRegdate BETWEEN #{startDate} AND #{endDate}
AND inquiryStatus = 1
AND inquiryStatus = 0
LIMIT #{start}, #{pageSize}

SELECT COUNT(*) from inquiry WHERE inquiryStatus = 1
AND inquiryTitle LIKE CONCAT('%' #{keyword}, '%')
AND inquiryRegdate BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) from inquiry WHERE inquiryStatus = 0
AND inquiryTitle LIKE CONCAT('%' #{keyword}, '%')
AND inquiryRegdate BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE inquiry
SET inquiryReply = #{inquiryReply},
	inquiryStatus = #{inquiryStatus},
	adminId = #{adminId}
WHERE inquiryId = #{inquiryId}
