mapper.select:
SELECT COUNT(*) FROM post
WHERE 1 = 1

    AND postTitle LIKE CONCAT('%', #{keyword}, '%')


    AND postType = #{postType}


    AND postRegdate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)


	AND userId = #{userId}

mapper.update:
UPDATE post
SET postTitle = #{postTitle},
    postContent = #{postContent},
    postType = #{postType}
WHERE postId = #{postId}

mapper.select:
	SELECT COUNT(*) FROM comment WHERE userId=#{userId}

        AND comContent LIKE CONCAT('%', #{keyword}, '%')


        AND comRegdate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)

SELECT c.commentId, c.userId, c.postId, c.comContent, c.comRegdate, p.postTitle FROM comment c JOIN post p ON c.postId = p.postId WHERE c.userId = #{userId}
AND c.comContent LIKE CONCAT('%', #{keyword}, '%')
AND c.comRegdate BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
ORDER BY c.comRegdate DESC LIMIT #{start}, #{pageSize}

mapper.update:
UPDATE comment
SET com_ref = #{commentId}
WHERE commentId = #{commentId}

UPDATE comment
SET com_re_step = com_re_step + 1
WHERE com_ref = #{com_ref}
AND com_re_step > #{com_re_step}
