mapper.insert:
INSERT INTO bookmark (
  userId, contentId, contentTypeId,
  title, firstimage, mapX, mapY, addr1
) VALUES (
  #{userId}, #{contentId}, #{contentTypeId},
  #{title}, #{firstimage}, #{mapX}, #{mapY}, #{addr1}
)

mapper.select:
SELECT COUNT(*)
FROM bookmark
WHERE contentId = #{contentId}
