mapper.update:
UPDATE user
SET petName = #{petName},
petType =
#{petType}
WHERE userId = #{userId}

UPDATE user
SET
userImage = #{imagePath},
userImageOrigin = #{originalFilename}
WHERE userId = #{userId}

UPDATE user
SET
email = #{email},
userTel = #{userTel}
WHERE userId = #{userId}

UPDATE user
SET userStatus = 'WITHDRAWN'
WHERE userId = #{userId}

UPDATE user
SET pwd = #{encodedPwd}
WHERE userId =
#{userId}

mapper.insert:
INSERT INTO inquiry (
inquiryTitle,
userId,
inquiryContent
) VALUES (
#{inquiryTitle},
#{userId},
#{inquiryContent}
)

mapper.delete:
DELETE FROM inquiry
WHERE inquiryId = #{inquiryId}
AND
userId = #{userId}
AND inquiryReply IS NULL
