mapper.insert:
INSERT INTO user (
    userId, userName, userTel, userNickname,
    userImage, petName, petType, pwd, email, isAdmin, userStatus
)
VALUES (
    #{userId}, #{userName}, #{userTel}, #{userNickname},
    #{userImage}, #{petName}, #{petType}, #{pwd}, #{email}, #{isAdmin}, #{userStatus}
)

mapper.update:
UPDATE user
SET pwd = #{pwd}
WHERE userId = #{userId}
